import { default as slugify } from 'slugify';

// while this class don't have any methods,
// It's will be useless
export class Job {

    constructor(
        private _name: string
    ) {
        if (!this._name) {
            throw new Error('Invalid argument name: string');
        }
    }

    public getName(): string {
        return this._name;
    }

    public getSlugifyName(): string  {
        return slugify(
            this.getName(),
            {
                replacement: '.', // replace spaces with replacement character, defaults to `-`
                lower: true,      // convert to lower case, defaults to `false`
                remove: /[*+~.()'"!:@?%$]/g, // Remove these chartes matching regex
                strict: true,    // strip special characters except replacement, defaults to `false`
            }
        );
    }

    public equals(obj: unknown): boolean {
        if(typeof(obj) !== typeof(this)) {
            return false
        }

        const compareJob: Job = obj as Job;

        if(this.getSlugifyName() === compareJob.getSlugifyName()) {
            return true;
        }
        return false;
    }
}

import { Password } from '../../src/domain/user/Password';

import sha256 from 'crypto-js/sha256';

export const genPassword = (): Password => {
    const hashedPassword = sha256('P@ssw0rd');

    const expirationDate = new Date();
    expirationDate.setMonth(expirationDate.getMonth() + 8);

    return new Password(hashedPassword.toString(), expirationDate);
};

export class Password {

    private readonly DATE_LIMIT_MONTH = 4;

    constructor(
        private _value: string,
        private _dateLimit: Date = null
    ) {
        if (!_value) {
            throw new Error('Invalid arguement value: string');
        }

        if (this._dateLimit) {
            if (this.isExpired()) {
                throw new Error('Invalid arguement dateLimit: cannot be in past');
            }
        } else {
            this._initializeDateLimit();
        }
    }

    public getValue(): string {
        return this._value;
    }

    public hasSameValue(password: Password): boolean {
        return this._value === password.getValue();
    }

    public isExpired(): boolean {
        return this._dateLimit <= new Date();
    }

    public isValueValid(valueToCheck: string): boolean {
        return !this.isExpired() && valueToCheck === this._value;
    }

    private _initializeDateLimit(): void {
        const now = new Date();
        this._dateLimit = new Date(now.setMonth(now.getMonth() + this.DATE_LIMIT_MONTH));
    }
}

